<?php

namespace YallaWebsite\ModBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * SideArticleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SideArticleRepository extends EntityRepository
{
    public function getSideArticles()
    {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT s FROM YallaWebsiteModBundle:SideArticle s WHERE s.position IN(1,2,3,4) ORDER BY s.position ASC'
            )
            ->getResult();
    }

    public function getPosition($pos)
    {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT s FROM YallaWebsiteModBundle:SideArticle s WHERE s.position = :pos'
            )
                ->setParameter('pos', $pos)
                ->getOneOrNullResult();
    }
}
